CustomField.formula:
If( OR(Total_Contract_Risk__c >= 25 ),
			IMAGE('/resource/RAGFlagRed',' '),

			If(  AND( Total_Contract_Risk__c >=12,Total_Contract_Risk__c <=24),
						IMAGE('/resource/RAGFlagAmber',' '),

						If ( ISPICKVAL(Status__c, 'Expired'),
										IMAGE('/resource/RAGFlagGIF',' '),

										If ( ISPICKVAL(Status__c, 'At Risk'),
														IMAGE('/resource/RAGFlagRed',' '),

														If(  Total_Contract_Risk__c <= 11,
																	IMAGE('/resource/RAGFlagGreen',' '),"None"
																)
													)
									)
					)

			)
